import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import HelloWorld from './HelloWorld';

function RecipientList() {
  const navigate = useNavigate();
  const [selectedRecipient, setSelectedRecipient] = useState(null);

  const recipients = [
    { id: 1, name: 'Â±±Áî∞ Â§™ÈÉé', icon: 'üßë' },
    { id: 2, name: 'Èà¥Êú® ‰∏ÄÈÉé', icon: 'üßî' },
    { id: 3, name: '‰ΩêËó§ Ëä±Â≠ê', icon: 'üë©' },
  ];

  if (selectedRecipient) {
    return <HelloWorld />;
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', height: '100vh', justifyContent: 'center', padding: '20px' }}>
      <h1 style={{ marginBottom: '20px' }}>ÈÄÅÈáëÁõ∏Êâã„ÇíÈÅ∏Êäû</h1>
      <div style={{ width: '100%', maxWidth: '400px' }}>
        {recipients.map(recipient => (
          <button
            key={recipient.id}
            style={{
              display: 'flex',
              alignItems: 'center',
              width: '100%',
              marginBottom: '10px',
              padding: '10px',
              background: '#f0f0f0',
              border: '1px solid #ccc',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
            onClick={() => setSelectedRecipient(recipient)}
          >
            <span style={{marginRight:'10px',fontSize: '24px'}}>
              {recipient.icon}
            </span>
            <span>{recipient.name}</span>
          </button>
        ))}
      </div>
      <button 
        onClick={() => navigate('/')}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          background: '#007bff',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer'
        }}
      >
        Êàª„Çã
      </button>
    </div>
  );
}

export default RecipientList;
